<script>
export default {
  name: "Calculator",
  data() {
    return {
      transfer: "",
      input: "",
      addNumber: "",
      divideNumber: "",
      reduceNumber: "",
      multiplyNumber: "",
      percentNumber: "",
      flag: 0,
    };
  },
  mounted() {
    /*  console.log(parseFloat(2) / parseFloat(2)); */
  },
  methods: {
    ToDeepLearning() {
      this.$router.push({
        name: "DeepLearning",
      });
    },
    change0() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      if (this.input == "0") {
        this.input = "0";
      } else {
        this.input += "0";
        this.flag = 0;
      }
    },
    change1() {
      //如果这里是由运算符在操作那么 就要重置input
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "1";
      this.flag = 0;
    },
    change2() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "2";
      this.flag = 0;
    },
    change3() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "3";
      this.flag = 0;
    },
    change4() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "4";
      this.flag = 0;
    },
    change5() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "5";
      this.flag = 0;
    },
    change6() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "6";
      this.flag = 0;
    },
    change7() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "7";
      this.flag = 0;
    },
    change8() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "8";
      this.flag = 0;
    },
    change9() {
      if (this.transfer) {
        this.input = "";
        this.transfer = "";
      }
      this.input = this.input + "9";
      this.flag = 0;
    },
    changeDot() {
      /* -1是 不在的意思 */
      if (this.input.indexOf(".") == -1) this.input = this.input + ".";
    },
    // 加
    add() {
      if (this.flag == 1 || this.flag == 0) {
        this.equal();
        this.addNumber = this.input;
        this.transfer = this.input;
        this.flag = 1;
      } else {
        this.$message("请输入数字在做运算");
      }
    },
    //减
    reduce() {
      if (this.flag == 2 || this.flag == 0) {
        this.equal();
        if (!this.input) {
          this.input += "-";
        } else {
          this.reduceNumber = this.input;
          this.transfer = this.input;
          this.flag = 2;
        }
      } else {
        this.$message("请输入数字在做运算");
      }
    },
    //乘
    multiply() {
      if (this.flag == 3 || this.flag == 0) {
        this.equal();
        this.multiplyNumber = this.input;
        this.transfer = this.input;
        this.flag = 3;
      } else {
        this.$message("请输入数字在做运算");
      }
    },
    //除
    divide() {
      if (this.flag == 4 || this.flag == 0) {
        this.equal();
        this.divideNumber = this.input;
        this.transfer = this.input;
        this.flag = 4;
      } else {
        this.$message("请输入数字在做运算");
      }
    },
    //清空所有
    AC() {
      this.input = "";
      this.addNumber = "";
      this.divideNumber = "";
      this.reduceNumber = "";
      this.multiplyNumber = "";
      this.percentNumber = "";
      this.transfer = "";
      this.flag = 0;
    },
    //百分号
    percent() {
      if (!this.input) {
        this.$message("请输入一个数字进行运算");
        this.AC();
      } else {
        if (this.flag == 5 || this.flag == 0) {
          this.percentNumber += "100";
          this.input += "%";
          this.flag = 0;
        } else {
          this.$message("请输入数字在做运算");
        }
      }
    },
    //开根号
    sqrt() {
      if (!this.input) {
        this.$message("请输入一个数字进行运算");
        this.AC();
      } else {
        if (this.flag == 6 || this.flag == 0) {
          this.equal();
          if (this.input < 0) {
            this.$message("负数没有平方根");
            this.input = "";
          } else {
            this.input = Math.sqrt(parseFloat(this.input));
            this.flag = 0;
          }
        } else {
          this.$message("请输入数字在做运算");
        }
      }
    },
    //删除一个字符
    deleteNumber() {
      this.input = this.input.slice(0, -1);
    },
    // 等于
    equal() {
      let {
        addNumber,
        reduceNumber,
        multiplyNumber,
        divideNumber,
        percentNumber,
      } = this;
      /* parseInt把任何形式的字符串转化为整数形式 */
      if (!this.input) {
        this.$message("请输入一个数字进行运算");
        this.AC();
      } else {
        if (addNumber) {
          this.input = `${parseFloat(this.input) + parseFloat(addNumber)}`;
          this.addNumber = "";
        } else if (reduceNumber) {
          this.input = `${parseFloat(reduceNumber) - parseFloat(this.input)}`;
          this.reduceNumber = "";
        } else if (multiplyNumber) {
          this.input = `${parseFloat(this.input) * parseFloat(multiplyNumber)}`;
          this.multiplyNumber = "";
        } else if (divideNumber) {
          if (parseFloat(this.input) == 0) {
            this.$message("除数不能为0");
            this.input = "";
          } else {
            this.input = `${parseFloat(divideNumber) / parseFloat(this.input)}`;
            this.divideNumber = "";
          }
        } else if (percentNumber) {
          this.input = `${parseFloat(this.input) / parseFloat(percentNumber)}`;
          this.percentNumber = "";
        }
      }
    },
  },
};
